package test.liuzehua.securitydemo.config;import com.alibaba.fastjson.JSONObject;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Component;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestControllerAdvice;/** * @author liuzehua * 2020/5/20 **/@Component@RestControllerAdvice@Slf4jpublic class AnyExceptionHandler {    @ExceptionHandler(value = Exception.class)    public String accessDeniedException(Exception e){        log.debug(e.getMessage());        return new JSONObject(){{put("code","4031");put("msg","你的权限不足");}}.toString();    }}